import React  from 'react'
import PropTypes from 'prop-types'

const ShopItemFunc = (props) => {
    const {item} = props;
    const {brand} = item;
    console.log(brand);
    console.log(item)
    return (
        <div className="main-content">
          <h2>{item.brand}</h2>
          <h1>{item.title}</h1>
          <h3>{item.description}</h3>
          <div className="description">
            {item.descriptionFull}
          </div>
          <div className="highlight-window mobile"><div className="highlight-overlay"></div></div>
          <div className="divider"></div>
          <div className="purchase-info">
            <div className="price">{item.currency}{item.price ? item.price.toFixed(2): undefined}</div>
            <button onClick={() => alert("Товар добавлен в корзину")}>Добавить в корзину</button>
          </div>
      </div>
    )
}

ShopItemFunc.propTypes = {
    item: PropTypes.shape({
        brand: PropTypes.string,
        title: PropTypes.string,
        description: PropTypes.string,
        descriptionFull: PropTypes.string,
        price: PropTypes.number,
        currency: PropTypes.oneOf(["£","$","€","¥"]),
    })
}

ShopItemFunc.defaultProps = {
        item: {
            brand: 'asd of asd',
        title: 'asd asd',
        description: 'asd coat in asd-blend',
        descriptionFull: 'aaaa in cotton-blend. Features a stand-up collar, concealed front closure and single back vent. Slim fit with clean, straight shape. Above-knee length.',
        price: 499,
     currency: '£'
        }

}

export default ShopItemFunc
